import React from 'react';
import '../style/Dashboard.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import PageNavbar from './PageNavbar';
import PostersBlock from './PostersBlock';


export default class Posters extends React.Component {
  constructor(props) {
    super(props);

    // The state maintained by this React Component. This component maintains the list of genres,
    // and a list of movies for a specified genre.
    this.state = {
      movies: []
    }
  }

  // React function that is called when the page load.
  componentDidMount() {
    // Send an HTTP request to the server.
    let poster;
    fetch("http://localhost:8081/posters",
      {
        method: 'GET' // The type of HTTP request.
      }).then(res => {
        // Convert the response data to a JSON.
        return res.json();
      }, err => {
        // Print the error if there is one.
        console.log(err);
      }).then(function (genreList) {
        if (!genreList) return;
        // Map each genreObj in genreList to an HTML element:
        // A button which triggers the showMovies function for each genre.
        poster = genreList;
        let genreDivs = genreList.map(function (MoviePosters, i) {
          let m_id = MoviePosters.imdb_id ;

          fetch("http://www.omdbapi.com/?i=" + m_id + "&apikey=28dd6ee",
            {
              method: 'GET' // The type of HTTP request.
            }).then(res => {
              // Convert the response data to a JSON.
              return res.json();
            }, err => {
              // Print the error if there is one.
              console.log(err);
            }).then(apiresult => {
              if (!apiresult) return;
              // Map each genreObj in genreList to an HTML element:
              // A button which triggers the showMovies function for each genre.
              return <PostersBlock moviename={MoviePosters.title} ratings={MoviePosters.rating}
            url={apiresult.url} />
            }, err => {
              // Print the error if there is one.
              console.log(err);
            });


        }

        );



        // Set the state of the genres list to the value returned by the HTTP response from the server.
        // this.setState({
        //   movies: genreDivs
        // });
      }, err => {
        // Print the error if there is one.
        console.log(err);
      });


  }


  render() {
    return (
      <div className="Dashboard">

        <PageNavbar active="dashboard" />

        <br></br>
        <div className="container movies-container">
          <div className="jumbotron">
            <div className="h5">Posters</div>
            <div className="genres-container">
              {this.state.movies}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
