import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class PostersBlock extends React.Component {
	constructor(props) {
		super(props);

		/* props looks like:
		{
			name
			poster
			rating
			url
		}
		*/
	}

	render() {
		return (
			<div className="genre"  >
			{this.props.moviename}
			<img src={this.props.url} herf={this.props.url}/>
			{this.props.ratings}
			</div>
		);
	}
}
