import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class PostersBlock extends React.Component {
	constructor(props) {
		super(props);

		/* props looks like:
		{
			name
			poster
			rating
			url
		}
		*/
	}

	render() {
		return (
			<div className="genre"  >
			<h1 align="center">{this.props.moviename}</h1>
			<img src={this.props.poster} onclick="window.open({this.props.url})"/>
			<h5 align="center">{this.props.ratings}</h5>
			</div>
		);
	}
}
