import React from 'react';
import PageNavbar from './PageNavbar';
import BestGenreRow from './BestGenreRow';
import '../style/BestGenres.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class BestGenre extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			selectedDecade: "",
			decades: [],
			genres: []
		};

		this.submitDecade = this.submitDecade.bind(this);
		this.handleChange = this.handleChange.bind(this);
	}

	/* ---- Q3a (Best Genres) ---- */
	componentDidMount() {
		fetch("http://localhost:8081/bestgenres",
		{
			method: "GET"
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}).then(decadesList => {
			console.log(decadesList); //displays your JSON object in the console

			let decadesDivs = decadesList.map(decades => 
				<option value={decades.decades}>_______</option>
			)

			//This saves our HTML representation of the data into the state, which we can call in our render function
			this.setState({
				decades: decadesDivs
			});
		});
	}

	handleChange(e) {
		this.setState({
			selectedDecade: e.target.value
		});
